import Layout from '../components/Layout';


const Home = () => (
    <Layout>
        <div id="bg">
            <img src="/image.jpg" alt="homepage image" />
               <div id="home-text-box">
                <h1>Welcome to my developer portfolio!</h1>
                <h2>I am Melissa, a Full Stack Web Developer from Cape Town, South Africa</h2>
                <h3>Let's get to know me</h3>
            </div>  
        </div>
        <button type="submit">Hello</button>
    <style jsx>{`
        #bg {
            top: -50%; 
            left: -50%; 
            width: 100%; 
            height: 200%;
        }
        #bg img {
            position: fixed; 
            top: 0; 
            left: 0; 
            right: 0; 
            bottom: 0; 
            margin: auto; 
            min-width: 50%;
            min-height: 50%;
            z-index: -1;
        }
        #home-text-box {
            border: none;
            padding: 10px;
            text-align: center;
            position: fixed;
            top: 50%;
            left: 50%;
            margin-left: -150px;
            margin-top: -50px;
            font-family: Comfortaa;
        }
        h1 {
            font-size: 35px;
        }


    `}</style>
    <style global jsx>{`
        html,
        body {
            font-family: Comfortaa;
        }
    `}</style>
    </Layout>


)

export default Home;
