import Link from 'next/link'

const headerStyle = {
    borderBottom: '1px solid black',
    fontVariantCaps: 'all-petite-caps',
    fontSize: '25px',
    paddingTop: '20px',
    paddingBottom: '20px',
}

const linkStyle = {
    textDecoration: 'none',
    margin: '15px',
    color: 'white'
}

const homeStyle = {
    textDecoration: 'none',
    margin: '15px',
    color: 'white',
    fontSize: '20px'
}

const Header = () => (
    <div style={headerStyle}>
        <Link href="/">
            <a style={homeStyle}>Melissa Appolis</a>
        </Link>
        <Link href="/about">
            <a style={linkStyle}>About</a>
        </Link>
        <Link href="/projects">
            <a style={linkStyle}>Projects</a>
        </Link>
        <Link href="/contact">
            <a style={linkStyle}>Contact</a>
        </Link>

<style jsx>{`
    a:hover {
        border-bottom: 2px dashed white;
        padding-bottom: 7px;
    }
`}</style>
    </div>
)

export default Header;
