import Link from 'next/link';
import Layout from '../components/Layout';
import Header from '../components/Header';
import Image from 'react-bootstrap/Image';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

const imageProject = {
    width: '600px'
}

const Projects = () => (
    <Layout>
        <Header />
        <div id="bg">
            <img id="background-image" src="/myimage2.jpg" alt="homepage image" />
            <div id="home-text-box">
                <h2>My Projects</h2>
            <Container >
                <Row id="container">
                    <Col >
                        <Image style={imageProject} id="image" src="/project1.png"  />
                        <div id="overlay">
                            <div id="overlay-text">View</div>
                        </div>
                    </Col>
                </Row>
                <br/>
                <Row>
                    <Col>
                        <Image style={imageProject} src="/project3.png" />
                    </Col>
                </Row>
                <br/>
                <Row>
                    <Col>
                        <Image style={imageProject} src="/project2.png" />
                    </Col>
                </Row>
            </Container>
            </div> 
        </div>
        <style jsx>{`
        #bg {
            top: -50%; 
            left: -50%; 
            width: 200%; 
            height: 200%;
            position: absolute;   
            z-index: -1;         
        }
        #bg img {
            top: 0; 
            left: 0; 
            right: 0; 
            bottom: 0; 
            margin: auto;
            min-width: 50%;
            min-height: 50%;
            position: fixed;
            width: 100%
        }
        #background-image {
            filter: blur(4px);
        }
        #home-text-box {
            border: none;
            padding-top: 250px;
            padding-bottom: 180px;
            padding-left: 50px;
            padding-right: 50px;
            position: absolute;
            top: 50%;
            left: 50%;
            margin-left: -350px;
            margin-top: -500px;
        }
        h2 {
            font-variant-caps: all-petite-caps;
        }
        a:hover {
            background-color: white;
            color: black !important;
        }
        #container {
            position: relative;
            width: 50%;
        }
        #image {
            opacity: 1;
            display: block;
            width: 100%;
            height: auto;
            transition: .5s ease;
            backface-visibility: hidden;
        }
        #overlay {
            transition: .5s ease;
            opacity: 0;
            position: absolute;
            top: 50%;
            left: 50%;
            transform: translate(-50%, -50%);
            -ms-transform: translate(-50%, -50%)
        }
        #container:hover #image {
            opacity: 0.3;
        }
        #container:hover #overlay {
            opacity: 0.3;
        }
        #overlay-text {
            background-color: #4CAF50;
            color: white;
            font-size: 16px;
            padding: 16px 32px;
        }
        `}</style>
    </Layout>
)

export default Projects;
