import Link from 'next/link'

const headerStyle = {
    fontVariantCaps: 'all-petite-caps',
    paddingTop: '10px',
    paddingBottom: '10px',
    backgroundColor: 'rgba(245, 245, 245, 0.651)',
    position: 'sticky'
}

const linkStyle = {
    textDecoration: 'none',
    margin: '15px',
    color: 'black',
    fontSize: '30px'
}

const homeStyle = {
    textDecoration: 'none',
    margin: '10px',
    color: 'rgb(173, 104, 63)',
    fontSize: '35px',
    fontWeight: 'bolder'
}

const Header = () => (
    <div style={headerStyle}>
        <Link href="/">
            <a style={homeStyle}>Melissa Appolis</a>
        </Link>
        <Link href="/about">
            <a className="link" style={linkStyle}>About</a>
        </Link>
        <Link href="/projects">
            <a className="link" style={linkStyle}>Projects</a>
        </Link>
        <Link href="/contact">
            <a className="link" style={linkStyle}>Contact</a>
        </Link>

<style jsx>{`
    .link:hover {
        border-bottom: 2px solid rgb(173, 104, 63);
        padding-bottom: 7px;
    }
    a:active {
        color: yellow;
    }
`}</style>
    </div>
)

export default Header;
