import Link from 'next/link';
import Layout from '../components/Layout';
import Header from '../components/Header';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

const imageStyle = {
    maxWidth: '95%',
}

const paragraphStyle = {
    textAlign: 'center',
    paddingTop: '200px',
}

const projectsLink = {
    textDecoration: 'none',
    color: 'rgb(212, 180, 34)',
    fontSize: '25px',
    fontVariantCaps: 'all-petite-caps',
    textAlign: 'center'
}

const About = () => (
    <Layout>
        <Header />
        <div id="bg">
            <img src="/myimage2.jpg" alt="homepage image" />
            <div id="home-text-box">
                <Container>
                    <Row>
                        <Col sm={6}>
                            <img style={imageStyle} src="/profile2.png" alt="about image" />
                        </Col>
                        <Col sm={6}>
                            <p style={paragraphStyle}>I am an aspiring Full Stack Web Developer in Cape Town, South Africa. I'm currently studying a course in Full Stack Web Development at Hyperion Dev, this is where my passion for design and development began. My goal is to develop as much skills as possible in web development to become a professional web developer. I have experience in Project Management and Event Management with a demonstrated history of working in the design and event industry.</p>
                            <Link href="/projects">
                                <a style={projectsLink}>My Projects</a>
                            </Link>
                        </Col>
                    </Row>
                </Container>
            </div> 
        </div>
        <style jsx>{`
        #bg {
            top: -50%; 
            left: -50%; 
            width: 200%; 
            height: 200%;
            position: fixed;   
            z-index: -1;         
        }
        #bg img {
            top: 0; 
            left: 0; 
            right: 0; 
            bottom: 0; 
            margin: auto;
            min-width: 50%;
            min-height: 50%;
            position: absolute;
            filter: grayscale(100%);
        }
        #home-text-box {
            border: none;
            padding-top: 250px;
            padding-bottom: 180px;
            padding-left: 50px;
            padding-right: 50px;
            position: fixed;
            top: 50%;
            left: 50%;
            margin-left: -550px;
            margin-top: -415px;
        }
        `}</style>
    </Layout>
)

export default About;
