import Link from 'next/link';
import Layout from '../components/Layout';
import Header from '../components/Header';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
// npm install --save-dev @iconify/react @iconify/icons-topcoat
import { Icon, InlineIcon } from '@iconify/react';
import githubIcon from '@iconify/icons-topcoat/github';
// npm install --save-dev @iconify/react @iconify/icons-il
import linkedinIcon from '@iconify/icons-il/linkedin';





const imageStyle = {
    maxWidth: '95%',
    filter: 'grayscale(70%)',
    borderRadius: '200px'
}

const paragraphStyle = {
    textAlign: 'center',
    fontSize: '25px',
    fontWeight: 'bolder',
    backgroundColor: 'rgba(64, 100, 24, 0.514)',
    padding: '300px 20px 200px 20px'
}

const projectsLink = {
    textDecoration: 'none',
    color: 'whitesmoke',
    fontSize: '25px',
    fontVariantCaps: 'all-petite-caps',
    textAlign: 'center',
    border: '1px solid white',
    padding: '3px 8px 8px 8px',
    letterSpacing: '2px'
}

const containerBox = {
    marginTop: '0px'
}

const Contact = () => (
    <Layout>
        <Header />
        <div id="bg">
            <img id="background-image" src="/myimage2.jpg" alt="homepage image" />
            <div id="home-text-box">
                <h1>Contact Me</h1>
                <p>074 905 6579</p>
                <a href="mailto:melissaappolis@gmail.com">melissaappolis@gmail.com</a>
                <h2>Social</h2>
                <p>
                    <a href="https://github.com/MelissaAppolis">
                        <Icon icon={githubIcon} />
                    </a> 
                    <a href="https://www.linkedin.com/in/melissa-appolis-109b6b117/">
                        <Icon icon={linkedinIcon} />
                    </a> 
                </p>
            </div> 
        </div>
        <style jsx>{`
        #bg {
            top: -50%; 
            left: -50%; 
            width: 200%; 
            height: 200%;
            position: fixed;   
            z-index: -1;         
        }
        #bg img {
            top: 0; 
            left: 0; 
            right: 0; 
            bottom: 0; 
            margin: auto;
            min-width: 50%;
            min-height: 50%;
            position: absolute;

        }
        #background-image {
            filter: blur(4px);
        }
        #home-text-box {
            border: none;
            padding-top: 250px;
            padding-bottom: 180px;
            padding-left: 50px;
            padding-right: 50px;
            position: fixed;
            top: 50%;
            left: 50%;
            margin-left: -50px;
            margin-top: -650px;
        }
        `}</style>
    </Layout>
)

export default Contact;
