import Link from 'next/link';
import Layout from '../components/Layout';
import Header from '../components/Header';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';

const projectTitle = {
    fontSize: '40px'
}

const projectText = {
    fontSize: '20px'
}

const projectButton = {
    fontVariantCaps: 'all-petite-caps',
    fontSize: '25px'
}

const Projects = () => (
    <Layout>
        <Header />
        <div id="bg">
            <img id="background-image" src="/myimage2.jpg" alt="homepage image" />
            <div id="home-text-box">
                <h2>My Projects</h2>
            <Card style={{ width: '800px' }}>
                <Card.Img variant="top" src="/project1.png"/>
                    <Card.Body>
                        <Card.Title style={projectTitle}>Online Clothing Store</Card.Title>
                        <Card.Text style={projectText}>
                            Baby Bouique Online Clothing Store that allows users to browse through product information and images, add products to cart, enter delivery details and checkout. This app was created with HTML, CSS and Javascript.
                        </Card.Text>
                        <Button style={projectButton} variant="outline-warning">View website</Button>
                    </Card.Body>
            </Card>
            <br/>
            <Card style={{ width: '800px' }}>
                <Card.Img variant="top" src="/project3.png"/>
                    <Card.Body>
                        <Card.Title>iTunes Search Engine App</Card.Title>
                        <Card.Text>
                            A fullstack app using React, Node.js and Express where a user can search for content within the iTunes Store and Apple Book Store by typing the name and selecting the type of content. The Search API is handled in the backend of this app.
                        </Card.Text>
                        <Button variant="primary">View website</Button>
                    </Card.Body>
            </Card>
            <br/>
            <Card style={{ width: '800px' }}>
                <Card.Img variant="top" src="/project2.png"/>
                    <Card.Body>
                        <Card.Title>Memory Game</Card.Title>
                        <Card.Text>
                            This is a memory game app built with React. It is a one player game where by the user can try and match up card numbers. The game is able to track game time as well as reset the game.
                        </Card.Text>
                        <Button variant="primary">View app</Button>
                    </Card.Body>
            </Card>
            </div> 
        </div>
        <style jsx>{`
        #bg {
            top: -50%; 
            left: -50%;
            width: 100%; 
            height: 200%;
            position: absolute;   
            z-index: -1;         
        }
        #bg img {
            top: 0; 
            left: 0; 
            right: 0; 
            bottom: 0; 
            margin: auto;
            min-width: 50%;
            min-height: 50%;
            position: fixed;
            width: 100%
        }
        #background-image {
            filter: blur(4px);
        }
        #home-text-box {
            border: none;
            padding-top: 250px;
            padding-bottom: 180px;
            padding-left: 50px;
            padding-right: 50px;
            position: absolute;
            top: 50%;
            left: 50%;
            margin-left: 250px;
            margin-top: -500px;
        }
        h2 {
            font-variant-caps: all-petite-caps;
            font-size: 45px;
            margin-bottom: 20px;
            color: rgb(139, 122, 46);
            text-align: center;
        }
        `}</style>
    </Layout>
)

export default Projects;
