import Link from 'next/link'

const headerStyle = {
    fontVariantCaps: 'all-petite-caps',
    fontSize: '25px',
    paddingTop: '20px',
    paddingBottom: '10px',
    backgroundColor: 'rgba(230, 111, 14, 0.514)'
}

const linkStyle = {
    textDecoration: 'none',
    margin: '15px',
    color: 'white'
}

const homeStyle = {
    textDecoration: 'none',
    margin: '10px',
    color: 'rgb(173, 104, 63)',
    fontSize: '35px',
    fontWeight: 'bolder'
}

const Header = () => (
    <div style={headerStyle}>
        <Link href="/">
            <a style={homeStyle}>Melissa Appolis</a>
        </Link>
        <Link href="/about">
            <a style={linkStyle}>About</a>
        </Link>
        <Link href="/projects">
            <a style={linkStyle}>Projects</a>
        </Link>
        <Link href="/contact">
            <a style={linkStyle}>Contact</a>
        </Link>

<style jsx>{`
    a:hover {
        border-bottom: 2px dashed white;
        padding-bottom: 7px;
    }
`}</style>
    </div>
)

export default Header;
