import Layout from '../components/Layout';
import Link from 'next/link';

const aboutLink = {
    textDecoration: 'none',
    color: 'rgb(212, 180, 34)',
    fontSize: '20px'
}

const Home = () => (
    <Layout>
        <div id="bg">
            <img src="/image12.png" alt="homepage image" />
            <div id="home-text-box">
                <h1>Hi</h1><h2>, I am Melissa...</h2>
                <h3>A Full Stack Web Developer</h3>
                <Link href="/about">
                    <a style={aboutLink}>Let's get to know me >></a>
                </Link>
            </div> 
        </div>
        <button type="submit">Hello</button>
    <style jsx>{`
        #bg {
            position: fixed;
            top: -50%; 
            left: -50%; 
            width: 100%; 
            height: 200%;
        }
        #bg img {
            position: absolute; 
            top: 0; 
            left: 0; 
            right: 0; 
            bottom: 0; 
            min-width: 50%;
            min-height: 50%;
            z-index: -1;
            width: 2000px;
        }
        #home-text-box {
            border: none;
            padding: 10px;
            position: fixed;
            top: 50%;
            left: 50%;
            margin-left: -200px;
            margin-top: -50px;
            font-family: Comfortaa;
        }
        h1 {
            color: rgb(11, 177, 135);
            font-weight: bold;
            display: inline-flex;
            font-size: 45px;
            margin: 5px;
        }
        h2 {
            display: inline-flex;
            font-size: 30px;
            margin: 5px;

        }
        h3 {
            font-size: 27px;
            margin-top: 0px;
            margin-bottom: 10px;
            font-weight: 100;
            color: rgb(11, 177, 135);
        }
    `}</style>
    <style global jsx>{`
        html,
        body {
            font-family: Comfortaa;
        }
    `}</style>
    </Layout>


)

export default Home;
